targets.queryText:
query ($sourceId: String!, $to: Long!) {
  queryStatusLast(sourceId: $sourceId, to: $to) {
    batteryVoltageAverage {
      dateMillis
      data
    }
  }
}

query ($sourceId: String!, $to: Long!) {
  data:queryStatusLast(sourceId: $sourceId, to: $to) {
    temperature {
      dateMillis
      fragmentIdString
      packet {
        temperatureFahrenheit
        identifier {
          representation
        }
      }
      meta {
        name
      }
    }
    roverStatus {
      dateMillis
      packet {
        batteryTemperatureFahrenheit
        identifier {representation}
      }
    }
  }
}

query ($sourceId: String!, $to: Long!) {
  data: queryStatusLast(to: $to, sourceId: $sourceId) {
