query ($sourceId: String!, $to: Long!) {
  today: querySolcastDay(to: $to, sourceId: $sourceId) {
    data: queryEnergyEstimate {
      dayStart
      dailyKWH
    }
  }
  tomorrow: querySolcastDay(to: $to, sourceId: $sourceId) {
    data: queryEnergyEstimate(offset: 1) {
      dayStart
      dailyKWH
    }
  }
  dayAfterTomorrow: querySolcastDay(to: $to, sourceId: $sourceId) {
    data: queryEnergyEstimate(offset: 2) {
      dayStart
      dailyKWH
    }
  }
}

query ($sourceId: String!, $to: Long!) {
  data: queryBatteryEstimate(to: $to, duration: "PT24H", sourceId: $sourceId) {
    data: queryEstimate(ratio: 0.3333) {
      dateMillis
      data
      identifiable {
        identifier {
          representation
        }
        identityInfo {
          displayName
        }
      }
    }
  }
}

